package com.cleanmvvm.starwars.data.dtoimport com.cleanmvvm.starwars.data.base.BaseDTOTestimport org.junit.Assertclass CharacterDetailsDTOTest : BaseDTOTest<CharacterDetailsDTO>(CharacterDetailsDTO::class.java) {    override var serverJson: String = """        {            "name": "Luke Skywalker",             "height": "172",             "birth_year": "19BBY",             "homeworld": "http://swapi.dev/api/planets/1/",             "species": [                "http://swapi.dev/api/species/2/"            ],            "films": [                "http://swapi.dev/api/films/1/",                 "http://swapi.dev/api/films/2/",                 "http://swapi.dev/api/films/3/",                 "http://swapi.dev/api/films/6/"            ],             "species": ["http://swapi.dev/api/species/2"],             "starships": [                "http://swapi.dev/api/starships/12/",                 "http://swapi.dev/api/starships/22/"            ]            }    """.trimIndent()    override fun assertClassUnderTestValues(classUnderTest: CharacterDetailsDTO) {        Assert.assertEquals("name should match", "Luke Skywalker", classUnderTest.name)        Assert.assertEquals("height should match", "172", classUnderTest.height)        Assert.assertEquals("birth year should match", "19BBY", classUnderTest.birth_year)        Assert.assertEquals("homeworld should match", "http://swapi.dev/api/planets/1/", classUnderTest.homeworld)        Assert.assertEquals("species should match", listOf("http://swapi.dev/api/species/2"), classUnderTest.species)        Assert.assertEquals("films should match", listOf("http://swapi.dev/api/films/1/",            "http://swapi.dev/api/films/2/",            "http://swapi.dev/api/films/3/",            "http://swapi.dev/api/films/6/" ), classUnderTest.films)    }}