package com.cleanmvvm.starwars.data.dtoimport com.cleanmvvm.starwars.data.base.BaseDTOTestimport org.junit.Assertclass MoviesDetailsDTOTest : BaseDTOTest<MoviesDetailsDTO>(MoviesDetailsDTO::class.java) {    override var serverJson: String = """     {       "title": "A New Hope",        "opening_crawl": "It is a period of civil war",        "release_date": "1977-05-25",     }    """.trimIndent()    override fun assertClassUnderTestValues(classUnderTest: MoviesDetailsDTO) {        Assert.assertEquals("title should match", "A New Hope", classUnderTest.title)        Assert.assertEquals("opening_crawl description should match", "It is a period of civil wa", classUnderTest.opening_crawl)        Assert.assertEquals("release date should match", "1977-05-25", classUnderTest.release_date)    }}