package com.cleanmvvm.starwars.data.dtoimport com.cleanmvvm.starwars.data.base.BaseDTOTestimport org.junit.Assertclass PlanetDetailsDTOTest : BaseDTOTest<PlanetDetailsDTO>(PlanetDetailsDTO::class.java) {    override var serverJson: String = """     {        "name": "Tatooine",        "population": "200000",     }    """.trimIndent()    override fun assertClassUnderTestValues(classUnderTest: PlanetDetailsDTO) {        Assert.assertEquals("name should match", "Tatooine", classUnderTest.name)        Assert.assertEquals("population should match", "200000", classUnderTest.population)    }}